CREATE TABLE PUBLIC.login (id INT AUTO_INCREMENT NOT NULL, login VARCHAR(50) NOT NULL, senha VARCHAR(70) NOT NULL, ativo BOOLEAN NOT NULL, cadastro_confirmado BOOLEAN NOT NULL, CONSTRAINT PK_LOGIN PRIMARY KEY (id));

CREATE TABLE PUBLIC.pessoa_fisica (id INT AUTO_INCREMENT NOT NULL, nome VARCHAR(100) NOT NULL, email VARCHAR(100) NOT NULL, celular VARCHAR(15) NOT NULL, telefone VARCHAR(14), data_nascimento date NOT NULL, cpf VARCHAR(14) NOT NULL, rg VARCHAR(20), tipo_pessoa VARCHAR(15) NOT NULL, endereco_logradouro VARCHAR(100) NOT NULL, endereco_numero VARCHAR(10) NOT NULL, endereco_complemento VARCHAR(100), endereco_cep VARCHAR(10) NOT NULL, endereco_bairro VARCHAR(80) NOT NULL, endereco_estado CHAR(2) NOT NULL, endereco_cidade VARCHAR(100) NOT NULL, login_id INT NOT NULL, CONSTRAINT PK_PESSOA_FISICA PRIMARY KEY (id));
